mapper.select:
SELECT t2.comm_name, t1.index_name,sum(t1.index_val) index_val ,t1.index_unit from wp_base_index_val t1 left join pss_comm_total t2 on t1.comm_id=t2.comm_id where t1.comm_id=#{condition.commId} and t1.index_type=#{condition.indexType}
and t1.data_time >= DATE_FORMAT(#{condition.dateFrom},'%Y-%m-%d %H-%i-%s')
and t1.data_time <= DATE_FORMAT(#{condition.dateTo},'%Y-%m-%d %H-%i-%s') group by t1.index_name ,t1.index_unit order by t1.index_name

SELECT t2.comm_name, t1.index_name,t1.index_val index_val ,t1.index_unit,t1.data_time from wp_base_index_val t1 left join pss_comm_total t2 on t1.comm_id=t2.comm_id where t1.comm_id=#{condition.commId} and t1.index_type=#{condition.indexType}
and t1.data_time >= DATE_FORMAT(#{condition.dateFrom},'%Y-%m-%d %H-%i-%s')
and t1.data_time <= DATE_FORMAT(#{condition.dateTo},'%Y-%m-%d %H-%i-%s') order by t1.index_name,data_time

select * from wp_base_index_val wiv
where index_type = #{indexType}
and comm_id= #{commId}
and area_name='全国'
and date  <=  #{date}
limit 0,30

select * from wp_base_index_val wiv
where index_type = #{indexType}
and comm_id= #{commId}
and area_name='全国'
order by date desc
limit 0,2

select val.*,total.comm_name
from wp_base_index_val val
left join pss_comm_total total
on val.comm_id = total.comm_id
where val.comm_id in (select comm_id from pss_comm_total where data_flag=0 and parent_code = #{param.commId})
and val.index_type = #{param.indexType}

    AND val.date  >=  #{param.startDate}


    AND val.date  <=  #{param.endDate}
