mapper.select:
SELECT t2.comm_name, t1.index_name,sum(t1.index_val) index_val ,t1.index_unit from wp_comm_index_val t1 left join pss_comm_total t2 on t1.comm_id=t2.comm_id where t1.comm_id=#{condition.commId} and t1.index_type=#{condition.indexType}
and t1.data_time >= DATE_FORMAT(#{condition.dateFrom},'%Y-%m-%d %H-%i-%s')
and t1.data_time <= DATE_FORMAT(#{condition.dateTo},'%Y-%m-%d %H-%i-%s') group by t1.index_name ,t1.index_unit order by t1.index_name

SELECT t2.comm_name, t1.index_name,t1.index_val index_val ,t1.index_unit,t1.data_time from wp_comm_index_val t1 left join pss_comm_total t2 on t1.comm_id=t2.comm_id where t1.comm_id=#{condition.commId} and t1.index_type=#{condition.indexType}
and t1.data_time >= DATE_FORMAT(#{condition.dateFrom},'%Y-%m-%d %H-%i-%s')
and t1.data_time <= DATE_FORMAT(#{condition.dateTo},'%Y-%m-%d %H-%i-%s') order by t1.index_name,data_time

SELECT max(data_time) as data_time,stat_area_name,stat_area_id,stat_frequency,index_val,index_unit
from wp_comm_index_val val
WHERE comm_id = #{commId}
AND index_type = #{indexType}
AND index_i = #{indexId}
AND data_time IS NOT NULL
GROUP BY stat_area_id
